import React, { useRef, useState, } from 'react';
import { Container, Typography, TextField, Button, Grid, Checkbox, InputAdornment, IconButton } from "@mui/material";
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { Link, useNavigate } from 'react-router-dom';
import styles from '../resources/FormStyles.module.css';
import { validateForm } from '../utility/FormValidation';
import Constants from "../utility/Constants"
import { makeRequest } from '../utility/ServerUtil';

function Register() {
    const navigate = useNavigate();
    //Form Refs
    const fNameRef = useRef();
    const lNameRef = useRef();
    const emailRef = useRef();
    const passwordRef = useRef();
    const cPasswordRef = useRef();

    //Use States
    const [errors, setErrors] = useState(Constants.FORM_ERRORS);
    const [helperTexts, setHelperTexts] = useState(Constants.FORM_HELPER_TEXTS);
    const [checked, setChecked] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    //Handle Showing Passwords
    const handleClickShowPassword = () => setShowPassword(!showPassword);

    //Handle Checkbox Clicks
    const handleCheckbox = (event) => {
        setChecked(event.target.checked);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        
        setErrors(Constants.FORM_ERRORS);
        setHelperTexts(Constants.FORM_HELPER_TEXTS);
        console.log("Error: " + errors.firstname);
        const firstName = fNameRef.current.value;
        const lastName = lNameRef.current.value;
        const email = emailRef.current.value;
        const password = passwordRef.current.value;
        const confirmPassword = cPasswordRef.current.value;

        const formData = {
            firstname: firstName,
            lastname: lastName,
            email: email,
            password: password,
            confirmPassword: confirmPassword,
            isChecked: checked,
        }

        const validationStatus = validateForm(formData, Constants.FORM_TYPE.register);

        
        // if (validationStatus.firstNameErrorMsg) {
        //     vFormErrors.firstName = true;
        //     vFormHelperTexts.firstName = validationStatus.firstNameErrorMsg;
        // }

        // if (validationStatus.lastNameErrorMsg) {
        //     vFormErrors.lastname = true;
        //     vFormHelperTexts.lastname = validationStatus.lastNameErrorMsg;
        // }

        // if (validationStatus.emailErrorMsg) {
        //     vFormErrors.email = true;
        //     vFormHelperTexts.email = validationStatus.emailErrorMsg;
        // }

        // if (validationStatus.passwordErrorMsg) {
        //     vFormErrors.password = true;
        //     vFormHelperTexts.password = validationStatus.passwordErrorMsg;
        // }

        // if (validationStatus.confirmPasswordErrorMsg) {
        //     vFormErrors.confirmPassword = true;
        //     vFormHelperTexts.confirmPassword = validationStatus.confirmPasswordErrorMsg;
        // }

        // if (validationStatus.checkboxErrorMsg) {
        //     alert(validationStatus.checkboxErrorMsg);
        // }
        setErrors(validationStatus.errors);
        setHelperTexts(validationStatus.helperTexts);

        console.log("Error After: " + errors.firstname);
        console.log(validationStatus.errors);
        if (validationStatus.canSubmit) {
            const data = {
                firstname: firstName,
                lastname: lastName,
                email: email,
                password: password,
            }
            makeRequest(Constants.REGISTER_API, data, Constants.SERVER_CALL_TYPE.post)
                .then((response) => {
                    console.log("Server Response: " + response);
                    if (response.status === Constants.STATUS_CODE_SUCCESS) {
                        console.log(response.data)
                        if (response.data.statusCode === Constants.STATUS_CODE_SUCCESS) {
                            console.log("Registraion Response Success");
                            alert(response.data.message);
                            navigate(Constants.LOGIN_PAGE);
                        }
                        else if (response.data.statusCode === Constants.STATUS_CODE_ERROR) {
                            console.log("Registration Response Error");
                            alert(response.data.message);
                        }
                    }
                    else {
                        console.log("Registration Response Error");
                        alert(Constants.REG_FORM_ERROR_MESSAGE);
                    }
                });
        }
    }

    return (
        <Container maxWidth='xs' className={styles.container}>
            <div className={styles.heading}>
                <Typography variant='h5' className={styles.headertext}>Sign Up</Typography>
                <Typography variant='subtitle1' className={styles.smalltext}>Please fill to create a new account</Typography>
            </div>
            <form autoComplete='off'
                noValidate
                onSubmit={handleSubmit}>
                <Grid
                    container
                    rowSpacing={0}
                    className={styles.namerow}
                    columnSpacing={0.5}
                    direction="row"
                    justifyContent="space-around"
                    alignItems="center"
                >
                    <Grid item xs={6}>
                        <TextField
                            sx={{ m: "2rem" }}
                            label='First Name'
                            placeholder='Ann'
                            variant='outlined'
                            error={errors.firstname}
                            helperText={helperTexts.firstname}
                            inputRef={fNameRef}
                            inputProps={Constants.TEXTFIELD_STYLING}
                        />
                    </Grid>
                    <Grid item xs={6}>
                        <TextField
                            type='text'
                            variant='outlined'
                            placeholder='Harrison'
                            label='Last Name'
                            error={errors.lastname}
                            helperText={helperTexts.lastname}
                            inputRef={lNameRef}
                            inputProps={Constants.TEXTFIELD_STYLING}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            type='email'
                            variant='outlined'
                            placeholder='user@example.com'
                            label='Email'
                            fullWidth
                            error={errors.email}
                            helperText={helperTexts.email}
                            inputRef={emailRef}
                            inputProps={Constants.TEXTFIELD_STYLING}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            type='password'
                            variant='outlined'
                            label='Password'
                            fullWidth
                            error={errors.password}
                            helperText={helperTexts.password}
                            inputRef={passwordRef}
                            inputProps={Constants.TEXTFIELD_STYLING}
                            InputProps={{
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <IconButton
                                            aria-label="toggle password visibility"
                                            onClick={handleClickShowPassword}
                                        //onMouseDown={handleMouseDownPassword}
                                        >
                                            {showPassword ? <Visibility /> : <VisibilityOff />}
                                        </IconButton>
                                    </InputAdornment>
                                )
                            }}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            type='password'
                            variant='outlined'
                            label='Confirm Password'
                            fullWidth
                            error={errors.confirmPassword}
                            helperText={helperTexts.confirmPassword}
                            inputRef={cPasswordRef}
                            inputProps={Constants.TEXTFIELD_STYLING}
                        />
                    </Grid>
                </Grid>
                <div className={styles.row}>
                    <Checkbox
                        sx={{
                            color: '#2139cd',
                            '&.Mui-checked': {
                                color: 'primary',
                            },
                        }}
                        onChange={handleCheckbox}
                    />
                    <div>I accept the <b>Terms of Use &amp; Privacy Policy</b></div>
                </div>
                <Button className={styles.submitButton} type='submit'>Register</Button>
            </form>
            <div>
                <Typography variant='subtitle1' className={styles.smalltext}>Already have an account? <Link to='/'><b>Login</b></Link></Typography>
            </div>
        </Container>

    )
}
export default Register;
