import React from 'react';
import { Typography } from '@mui/material';
import { useDropzone } from 'react-dropzone';
import { useNavigate } from 'react-router-dom';
import styles from '../resources/Main.module.css';
import Layout from './Layout';
import Constants from '../utility/Constants';
function Main() {
    const navigate = useNavigate();
    //const [files, setFiles] = useState([]);
    const {
        getRootProps,
        getInputProps,
        isDragAccept,
        isDragReject,
        isDragActive,
    } = useDropzone({
        accept: ["image/png", "image/jpeg"],
        onDrop: (acceptedFiles, fileRejections) => {
            if(acceptedFiles){
            acceptedFiles.map(file =>
                Object.assign(file, { preview: URL.createObjectURL(file) })
            )
            console.log("Acccepted Files: ", acceptedFiles);
            uploadFiles(acceptedFiles);}
        }
    })

    const uploadFiles = (files) => {
        if (files.length < 1) {
            alert("Please Upload .PNG or .JPG Images");
            return;
        }
        console.log("Sending: ", files);
        navigate(Constants.STATUS_PAGE,
            { state: { files: files } },
            { replace: true });

    }

    return (
        <Layout isHome={true}>
            <div className={styles.modal}>
                <div className={styles.heading}>
                    <Typography variant='h6'>Upload Images</Typography>
                    <Typography variant='subtitle2'>
                        For a better prediction please upload multiple images
                    </Typography>
                </div>
                <div className={styles['file-upload-container']}>
                    <div {...getRootProps()} className={styles['inner-file-upload-container']}>
                        <input {...getInputProps()} />
                        <div className={styles['file-upload-caption']}>
                            <Typography variant='p'>
                                Drag and Drop Images or click to browse device
                            </Typography>
                        </div>
                    </div>
                </div>
                <div className={styles.footer}>
                    <Typography variant='subtitle2'>
                        Only .png and .jpg images are allowed
                    </Typography>
                </div>
            </div>
        </Layout>
    );
}
export default Main;
